mapper.select:
select
    ,
    s.name as categoryName,
    d.name as typeName,
    t.name as publishUnitName,
    b.name as sourceName
from
    tb_law l
left join tb_system_data_type s on s.type = 3 and s.value = l.category and s.code = #{code} and s.param = #{categoryCode}
left join tb_system_data_type d on d.type = 3 and d.value = l.type and d.code = #{code} and d.param = #{typeCode}
left join tb_system_data_type t on t.type = 3 and t.value = l.publish_unit and t.code = #{code} and t.param = #{publishUnitCode}
left join tb_system_data_type b on b.type = 3 and b.value = l.source and b.code = #{code} and b.param = #{sourceCode}
where
    l.usable = 1

        and l.id != #{currentId}


        and l.category = #{category}


        and l.type = #{type}


        and l.publish_unit = #{publishUnit}


        and l.status = #{status}


        and l.source = #{source}


        and l.implement_date between #{startDate} and #{endDate}


        and l.name like concat('%', #{searchPhrase}, '%')

order by l.update_time desc

select
    ,
    s.name as categoryName,
    d.name as typeName,
    t.name as publishUnitName,
    b.name as sourceName
from
    tb_law l
left join tb_system_data_type s on s.type = 3 and s.value = l.category and s.code = #{code} and s.param = #{categoryCode}
left join tb_system_data_type d on d.type = 3 and d.value = l.type and d.code = #{code} and d.param = #{categoryCode}
left join tb_system_data_type t on t.type = 3 and t.value = l.publish_unit and t.code = #{code} and t.param = #{publishUnitCode}
left join tb_system_data_type b on b.type = 3 and b.value = l.source and b.code = #{code} and b.param = #{sourceCode}
where
    l.usable = 1
    and l.id = #{id}
