mapper.select:
select
    ,
    s.sample as title,
    s.publish_date as publishTime
from
    tb_spot_check s
where
    s.usable = 1

        and s.id != #{currentId}


        and s.product_type = #{productType}


        and s.institution = #{institution}


        and s.check_result = #{checkResult}


        and s.publish_date >= #{startDate} and s.publish_date <= #{endDate}


        and (s.unit like concat('%', #{searchPhrase}, '%') or s.producer like concat('%', #{searchPhrase}, '%'))


    order by s.${orderName} ${orderType}


    order by s.update_time desc
