INSERT INTO `tb_criterion` VALUES (38, 'QB /T 5411- 2019 《化妆品中禁用物质二甘醇的测定  气相色谱法》', '1574147128835', '1574147128841', '8901e73aa83b5ae17a2c3d2e22b359e8', '1574147128846', '2019-12-02', '2020-04-01', '本标准适用于膏霜、水剂、乳液、啫喱类化妆品中二甘醇的测定。\n本标准的检出限和定量限：啫喱类、水剂类中二甘醇的检出限为200mg/kg，定量限为500 mg/kg；\n膏霜类和乳液类样品中二甘醇的检出限为400 mg/kg，定量限为600 mg/kg。', 1, '2019-12-10 16:28:52', 1, '2019-12-10 16:28:52', 1);
INSERT INTO `tb_criterion` VALUES (39, 'GB/T 28599-2012 《化妆品中邻苯二甲酸脂类物质的测定》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2012-06-29', '2012-12-01', '适用于化妆品中邻苯二甲酸脂类物质的测定，其中气相色谱-质谱联用法和高效液相色谱法适用于化妆品中22种邻苯二甲酸脂类化合物的测定，气相色谱法适用于化妆品（指甲油和唇膏除外）中18种邻苯二甲酸脂类化合物的测定。', 1, '2019-12-10 17:47:26', 1, '2019-12-10 17:47:26', 1);
INSERT INTO `tb_criterion` VALUES (40, 'GB/T 29659-2013 《化妆品中丙烯酰胺的测定》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、乳液、化妆水等皮肤护理类化妆品中丙烯酰胺的测定，高效液相色谱-二极管阵列检测器法对丙烯酰胺的检出限为30μg/kg，定量限为90μg/kg，高效液相色谱-质谱/质谱法对丙烯酰胺的检出限为5μg/kg，定量限为15μg/kg。', 1, '2019-12-10 17:49:16', 1, '2019-12-10 17:49:16', 1);
INSERT INTO `tb_criterion` VALUES (41, 'GB/T 29660-2013 《化妆品中总铬含量的测定》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于化妆品中总铬含量的测定，当称样量为1g时，火焰原子吸收分光光度法检出限为0.25mg/kg，定量限为0.83mg/kg，电感耦合等离子体质谱法检出限为0.005mg/kg，定量限为0.015mg/kg。', 1, '2019-12-10 17:50:22', 1, '2019-12-10 17:50:22', 1);
INSERT INTO `tb_criterion` VALUES (42, 'GB/T 29661-2013 《化妆品中尿素含量的测定 酶催化法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于化妆品中尿素含量的定量测定，其定量下限为0.025% 。', 1, '2019-12-10 17:51:38', 1, '2019-12-10 17:51:38', 1);
INSERT INTO `tb_criterion` VALUES (43, 'GB/T 29662-2013 《化妆品中曲酸、曲酸二棕榈酸酯的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于化妆品中曲酸的测定，检出限为0.01%，定量限为0.1%，适用于化妆品中曲酸二棕榈酸酯的测定，检出限为0.04%，定量限为0.4%。', 1, '2019-12-10 17:52:33', 1, '2019-12-10 17:52:33', 1);
INSERT INTO `tb_criterion` VALUES (44, 'GB/T 29663-2013 《化妆品中苏丹红Ⅰ、Ⅱ、Ⅲ、Ⅳ的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于唇膏、唇彩、粉、粉块、指甲油彩妆类化妆品中苏丹红Ⅰ、Ⅱ、Ⅲ、Ⅳ的测定，检出限为50μg/kg，定量限为150μg/kg。', 1, '2019-12-10 17:53:27', 1, '2019-12-10 17:53:27', 1);
INSERT INTO `tb_criterion` VALUES (45, 'GB/T 29664-2013 《化妆品中维生素B3（烟酸、烟酰胺）的测定  高效液相色谱法和高效液相色谱串联质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、乳液、化妆水、皂基如美容皂、蜡基如唇膏等化妆品中维生素B3（烟酸、烟酰胺）的定量测定，其中高效液相色谱法对烟酸、烟酰胺的检出限为2μg/g，定量限为6μg/g，高效液相色谱串联质谱法对烟酸、烟酰胺的检出限为100μg/kg，定量限为300μg/kg。', 1, '2019-12-10 17:54:36', 1, '2019-12-10 17:54:36', 1);
INSERT INTO `tb_criterion` VALUES (46, 'GB/T 29669-2013 《化妆品中N-亚硝基二甲基胺等10种挥发性亚硝胺的测定   气相色谱-质谱/质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-01', '适用于膏霜、散粉、唇膏、水剂和香波类化妆品中N-亚硝基二甲基胺等10种挥发性亚硝胺的测定，检出限和定量限:N-亚硝基二乙基胺、N-亚硝基二丙基胺、N-亚硝基吗啉、N-亚硝基哌啶、N-亚硝基二丁基胺、N-亚硝基二苯基胺、N-亚硝基二苄基胺的检出限为1.25 mg/kg,定量限为2.5mg/kg;N-亚硝基吡咯烷的检出限为2.5mg/kg,定量限为5.0mg/kg;N-亚硝基二甲基胺、N-亚硝基二环己基胺的检出限为5.0mg/kg,定量限为10.0mg/kg。', 1, '2019-12-10 17:55:35', 1, '2019-12-10 17:55:35', 1);
INSERT INTO `tb_criterion` VALUES (47, 'GB/T 29671-2013 《化妆品中苯酚磺酸锌的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于水剂、香波、膏霜、蜡基、散粉类化妆品中苯酚磺酸锌的测定，检出限和定量限:水剂类和香波类样品中检出限为24mg/kg,定量限为30mg/kg;膏霜类和蜡基类样品中检出限为120mg/kg,定量限为140mg/kg;散粉类样品中检出限为60mg/kg,定量限为90mg/kg。', 1, '2019-12-10 18:01:09', 1, '2019-12-10 18:01:09', 1);
INSERT INTO `tb_criterion` VALUES (48, 'GB/T 29672-2013 《化妆品中丙烯腈的测定 气相色谱-质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、水剂、散粉、香波、唇膏类化妆品中丙烯腈的测定，检出限为0.2mg/kg,定量限为0.4mg/kg。', 1, '2019-12-10 18:02:12', 1, '2019-12-10 18:02:12', 1);
INSERT INTO `tb_criterion` VALUES (49, 'GB/T 29673-2013 《化妆品中六氯酚的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、水剂、散粉、香波类化妆品中六氯酚的测定，检出限为10mg/kg,定量限为20mg/kg。', 1, '2019-12-10 18:43:52', 1, '2019-12-10 18:43:52', 1);
INSERT INTO `tb_criterion` VALUES (50, 'GB/T 29674-2013 《化妆品中氯胺T的测定高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、水剂、散粉、香波、牙膏类化妆品中氯胺T的测定， 不适用于添加了对甲苯磺酰胺的产品， 检出限为8.0mg/kg,定量限为16.5mg/kg。', 1, '2019-12-10 18:46:51', 1, '2019-12-10 18:46:51', 1);
INSERT INTO `tb_criterion` VALUES (51, 'GB/T 29675-2013 《化妆品中壬基苯酚的测定 液相色谱-质谱/质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、散粉、水剂、唇膏类化妆品中壬基苯酚的测定，检出限为0.1mg/kg,定量限为0.2mg/kg。', 1, '2019-12-10 19:03:51', 1, '2019-12-10 19:03:51', 1);
INSERT INTO `tb_criterion` VALUES (52, 'GB/T 29676-2013 《化妆品中三氯叔丁醇的测定 气相色谱-质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于膏霜、水剂、散粉、香波、唇膏类化妆品中三氯叔丁醇的测定，检出限为0.15mg/kg,定量限为0.4mg/kg。', 1, '2019-12-10 19:05:53', 1, '2019-12-10 19:05:53', 1);
INSERT INTO `tb_criterion` VALUES (53, 'GB/T 29677-2013 《化妆品中硝甲烷的测定 气相色谱-质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于水剂、散粉、香波、喷雾、唇膏类化妆品中硝甲烷的测定，检出限为0.1mg/kg,定量限为0.2mg/kg。', 1, '2019-12-10 19:06:51', 1, '2019-12-10 19:06:51', 1);
INSERT INTO `tb_criterion` VALUES (54, 'GB/T 30087-2013 《化妆品中保松泰含量的测定方法 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-12-17', '2014-03-01', '适用于水剂类、膏霜乳液类、唇膏类等化妆品中保泰松的测定。', 1, '2019-12-10 19:08:45', 1, '2019-12-10 19:08:45', 1);
INSERT INTO `tb_criterion` VALUES (55, 'GB/T 30088-2013 《化妆品中甲基丁香酚的测定 气相色谱法/质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-12-17', '2014-03-01', '适用于水剂类、膏霜乳液类、唇膏类等化妆品中甲基丁香酚的测定。', 1, '2019-12-10 19:09:50', 1, '2019-12-10 19:09:50', 1);
INSERT INTO `tb_criterion` VALUES (56, 'GB/T 30089-2013 《化妆品中氯磺丙脲、氯磺丁脲、甲苯磺丁脲的测定   液相色谱法/串联质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-12-17', '2014-03-01', '适用于水剂类、膏霜类、凝胶类、粉类及唇膏类化妆品中氯磺丙脲、氨磺丁脲、甲苯磺丁脲的测定。', 1, '2019-12-10 19:10:40', 1, '2019-12-10 19:10:40', 1);
INSERT INTO `tb_criterion` VALUES (57, 'GB/T 30926-2014 《化妆品中7种维生素C 衍生物的测定   高效液相色谱-串联质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于化妆品中7种维生素 C衍生物【抗坏血酸葡萄糖苷、3-O-乙基抗坏血酸、抗坏血酸磷酸酯盐(以抗坏血酸磷酸酯计)、抗坏血酸硬脂酸酯、抗坏血酸棕榈酸酯、抗坏血酸二棕榈酸酯、抗坏血酸四异棕榈酸酯】的定量测定，检出限均为3μg/g,对所有目标化合物定量限均为10μg/g。', 1, '2019-12-10 19:11:36', 1, '2019-12-10 19:11:36', 1);
INSERT INTO `tb_criterion` VALUES (58, 'GB/T 30927-2014 《化妆品中罗丹明B等4种禁用着色剂的测定   高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、散粉、水剂、香波和唇膏类化妆品中罗丹明 B、酸性黄36、颜料红53∶1和颜料橙5等4种禁用着色剂的测定，检出限和定量限:罗丹明B的检出限为2mg/kg,定量限为4mg/kg;散粉类、水剂类和唇膏类样品中酸性黄36的检出限为4mg/kg,定量限为8mg/kg,膏霜类和香波类样品中酸性黄26的检出限为3 mg/kg,定 量 限 为 6 mg/kg;散 粉 类、水 剂 类 和 唇 膏 类 样 品 中 颜 料 红 53∶1 的 检 出 限 为6mg/kg,定量限为10mg/kg,膏霜类和香波类样品中颜料红53∶1的检出限为4mg/kg,定量限为8mg/kg;颜料橙5的检出限为3mg/kg,定量限为6mg/kg。', 1, '2019-12-10 19:12:31', 1, '2019-12-10 19:12:31', 1);
INSERT INTO `tb_criterion` VALUES (59, 'GB/T 30929-2014 《化妆品中禁用物质2,4,6-三氯苯酚、五氯苯酚和硫氯酚的测定高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、乳液、液体类化妆品中2,4,6-三氯苯酚、五氯苯酚和硫氯酚含量的测定，检出限和定量限:2,4,6-三氯苯酚的检出限为1.5mg/kg,定量限为5.0mg/kg;五氯苯酚的检出限为1.5mg/kg,定量限为5.0mg/kg;硫氯酚的检出限为0.8mg/kg,定量限为2.5mg/kg。', 1, '2019-12-10 19:13:45', 1, '2019-12-10 19:13:45', 1);
INSERT INTO `tb_criterion` VALUES (60, 'GB/T 30930-2014 《化妆品中联苯胺等9种禁用芳香胺的测定高效液相色谱-串联质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、乳液、散粉、水剂等化妆品中芳香胺（联苯胺、4,4’-二氨基二苯醚、邻氨基苯甲醚、3,3’-二甲基联苯胺、2,4,5-三甲基苯胺、4,4’-二氨基二苯硫醚、5-硝基-邻甲苯胺、3,3’-二氯联苯胺、4-氨基偶氮苯）的测定，联苯胺、4,4’-二氨基二苯醚、邻氨基苯甲醚、3,3’-二甲基联苯胺、2,4,5-三甲基苯胺、4,4’-二氨基二苯硫醚、5-硝基-邻甲苯胺、4-氨基偶氮苯的最低检出限为0.05mg/kg,定量限为0.2mg/kg; 3,3’-二氯联苯胺的最低检出限为0.15mg/kg,定量限为0.5mg/kg。', 1, '2019-12-10 19:14:42', 1, '2019-12-10 19:14:42', 1);
INSERT INTO `tb_criterion` VALUES (61, 'GB/T 30931-2014 《化妆品中苯扎氯铵含量的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、香波、水剂类等化妆品中苯扎氯铵的测定，检出限为30mg/kg,定量限为100mg/kg。', 1, '2019-12-10 19:15:39', 1, '2019-12-10 19:15:39', 1);
INSERT INTO `tb_criterion` VALUES (62, 'GB/T 30932-2014 《化妆品中禁用物质二噁烷残留量的测定   顶空气相色谱-质谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、水剂、香波类化妆品中二噁烷残留量的测定，检出限为0.8mg/kg,定量限为2.5mg/kg。', 1, '2019-12-10 19:16:48', 1, '2019-12-10 19:16:48', 1);
INSERT INTO `tb_criterion` VALUES (63, 'GB/T 30933-2014 《化妆品中防晒剂二乙氨基羟苯甲酰基苯甲酸己酯的测定   高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、乳、液、蜡质类化妆品中防晒剂二乙氨基羟苯甲酰基苯甲酸己酯的测定，检出限为0.015%,定量限为0.05%。', 1, '2019-12-10 19:17:40', 1, '2019-12-10 19:17:40', 1);
INSERT INTO `tb_criterion` VALUES (64, 'GB/T 30934-2014 《化妆品中脱氢醋酸及其盐类的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、乳液、香波、化妆水、啫喱水、唇膏等化妆品中脱氢醋酸及其盐类(以脱氢醋酸计)的测定，检出限:膏 霜、乳液、香波、唇膏蜡基 类为0.002%;化妆水、啫喱水等水剂类为0.001%。定量限:膏霜、乳液、香波、唇膏蜡基类为0.006%;化妆水、啫喱水等水剂类为0.003%', 1, '2019-12-10 19:18:28', 1, '2019-12-10 19:18:28', 1);
INSERT INTO `tb_criterion` VALUES (65, 'GB/T 30935-2014 《化妆品中8-甲氧基补骨脂素等8种呋喃香豆素的测定   高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、散粉、蜡基、水剂和香波类化妆品中8-甲氧基补骨脂素等8种禁用呋喃香豆素的测定，检出限和定量限:8-羟基补骨脂素的检出限为0.1mg/kg,定量限为0.25mg/kg;补骨脂素、异补骨脂素、8-甲氧基补骨脂素、5-甲氧基补骨脂素、三甲沙林、欧前胡素、异欧前胡素的检出限为0.2mg/kg,定量限为0.5mg/kg。', 1, '2019-12-10 19:19:25', 1, '2019-12-10 19:19:25', 1);
INSERT INTO `tb_criterion` VALUES (66, 'GB/T 30936-2014 《化妆品中氯磺丙脲、甲苯磺丁脲和氨磺丁脲3种禁用磺脲类物质的测定方法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于膏霜、乳液、液体类等化妆品中氯磺丙脲、甲苯磺丁脲和氨磺丁脲的定量测定;液相色谱-质谱/质谱法同时适用于化妆品中磺脲类物质的定性确证，液相色谱法测定氯磺丙脲、甲苯磺丁脲、氨磺丁脲的检出限为20mg/kg,定量限为60mg/kg;液相色谱-质谱/质谱法测定氯磺丙脲、甲苯磺丁脲、氨磺丁脲的检出限为0.025mg/kg,定量限为0.080mg/kg。', 1, '2019-12-10 19:20:17', 1, '2019-12-10 19:20:17', 1);
