INSERT INTO `tb_criterion` VALUES (98, 'GB/T 35946-2018 《眼部化妆品中硫柳汞含量的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于卸妆液、眼影、眼霜、睫毛膏、眼线液、眼线膏、眉笔等眼部化妆品中硫柳汞含量的测定，卸妆液的检出限为1mg/kg，眼影、眼霜、眼线液等检出限为2mg/kg，卸妆液的定量限为3mg/kg，眼影、眼霜、眼线液等定量限为6mg/kg。', 1, '2019-12-10 19:51:36', 1, '2019-12-10 19:51:36', 1);
INSERT INTO `tb_criterion` VALUES (99, 'GB/T 35948-2018 《化妆品中7种4-羟基苯甲酸酯的测定 高效液相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于膏霜类、乳液类、啫喱类、水类、粉类、蜡基类化妆品中4-羟基苯甲酸丙酯、4-羟基苯甲酸异丙酯、4-羟基苯甲酸丁酯、4-羟基苯甲酸异丁酯、4-羟基苯甲酸苯酯、4-羟基苯甲酸苄酯和4-羟基苯甲酸戊酯等7种4-羟基苯甲酸酯的测定，7种4-羟基苯甲酸酯的方法检出限均为2mg/kg，7种4-羟基苯甲酸酯的方法定量限均为6mg/kg。', 1, '2019-12-10 19:52:39', 1, '2019-12-10 19:52:39', 1);
