INSERT INTO `tb_criterion` VALUES (104, 'GB/T 35953-2018 《化妆品中限用物质二氯甲烷和1,1,1-三氯乙烷的测定顶空气相色谱法》', '1574147128834', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于膏霜、乳、液、水类化妆品中二氯甲烷和1,1,1-三氯乙烷含量的测定，本标准对于二氯甲烷和1,1,1-三氯乙烷的检出限分别为0.070mg/kg和0.0010mg/kg，定量限分别为0.25mg/kg和0.0050mg/kg。', 1, '2019-12-10 19:59:13', 1, '2019-12-10 19:59:13', 1);
