INSERT INTO `tb_criterion` VALUES (121, 'QB/T 5292-2018 《化妆品中禁用物质维生素K1的测定  高效液相色谱法》', '1574147128835', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2018-10-22', '2019-04-01', '适用于化妆品中维生素K1的测定，检出限为1.5mg/kg，定量限为4.0mg/kg。', 1, '2019-12-10 20:23:32', 1, '2019-12-10 20:23:32', 1);
INSERT INTO `tb_criterion` VALUES (122, 'QB/T 5293-2018 《化妆品中禁用物质磷酸三丁酯、磷酸三（2-氯乙）酯和磷酸三甲酚酯的测定  气相色谱-质谱法》', '1574147128835', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2018-10-22', '2019-04-01', '适用于化妆品中禁用物质磷酸三丁酯、磷酸三（2-氯乙）酯和磷酸三甲酚酯的测定，检出限均为1.0mgkg，定量限均为3.5mg/kg。', 1, '2019-12-10 20:24:40', 1, '2019-12-10 20:24:40', 1);
INSERT INTO `tb_criterion` VALUES (123, 'QB/T 5294-2018 《化妆品中溴代和氯代水杨酰苯胺的测定  高效液相色谱法》', '1574147128835', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2018-10-22', '2019-04-01', '适用于膏霜类、水剂类、唇膏类、散粉类和香波类化妆品中34，5-三溴水杨酰苯胺、4.5.二溴水杨酰苯胺、3，5-二溴水杨酰苯胺、3，4，5-三氯水杨酰苯胺、3\'，4-二氯水杨酰苯胺、4，5-二氯水杨酰苯胺等6种溴代和氯代水杨酰苯胺的测定，6种溴代和氯代水杨酰苯胺的检出限为5mg/kg，定量限为10mg/kg。', 1, '2019-12-10 20:25:37', 1, '2019-12-10 20:25:37', 1);
INSERT INTO `tb_criterion` VALUES (124, 'QB/T 5295-2018 《美白化妆品中鞣花酸的测定  高效液相色谱法》', '1574147128835', '1574147128841', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2018-10-22', '2019-04-01', '适用于水类、乳液类、膏霜类美白化妆品中霖花酸的测定，检出限、定量限分别为4.0mg/kg、15mgkg。', 1, '2019-12-10 20:26:28', 1, '2019-12-10 20:26:28', 1);
INSERT INTO `tb_criterion` VALUES (125, 'GB/T 7917.1-1987 《化妆品卫生化学标准检验方法 汞》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '适用于化妆品中总汞的测定，本法最低检出量为0.01μg汞，若取1g样品测定，最低检测浓度为0.01ppm，本标准采用冷原子吸收分光光度法。', 1, '2019-12-10 20:28:55', 1, '2019-12-10 20:28:55', 1);
INSERT INTO `tb_criterion` VALUES (126, 'GB/T 7917.2-1987 《化妆品卫生化学标准检验方法 砷》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '适用于化妆品中总砷的测定。规定的两种方法最低检出量为0.5μg砷，若取1g样品测定，最低检测浓度为0.5ppm。', 1, '2019-12-10 20:30:02', 1, '2019-12-10 20:30:02', 1);
INSERT INTO `tb_criterion` VALUES (127, 'GB/T 7917.3-1987 《化妆品卫生化学标准检验方法 铅》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '适用于化妆品中铅的测定。本方法样品最低检测浓度为4PPm。', 1, '2019-12-10 20:31:05', 1, '2019-12-10 20:31:05', 1);
INSERT INTO `tb_criterion` VALUES (128, 'GB/T 7917.4-1987 《化妆品卫生化学标准检验方法 甲醇》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '适用于含乙醇的化妆品中甲醇含量的测定。', 1, '2019-12-10 20:32:07', 1, '2019-12-10 20:32:07', 1);
INSERT INTO `tb_criterion` VALUES (129, 'GB/T 7918.1-1987 《化妆品微生物标准检验方法 总则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '所采集的样品，应具有代表性，一般视每批化妆品数量大小，随机抽取相应数量的包装单位，检验时，应分别从两个包装单位以上的样品中共取10g或10ml，包装量小的样品，取样量可酌减。', 1, '2019-12-10 20:33:35', 1, '2019-12-10 20:33:35', 1);
INSERT INTO `tb_criterion` VALUES (130, 'GB/T 7918.2-1987 《化妆品微生物标准检验方法 细菌总数测定》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '测定细菌总数可用来判明化妆品被细菌污染的程度，以及生产单位所用的原料、工具设备、工艺流程、操作者的卫生状况，是对化妆品进行卫生学评价的综合依据。', 1, '2019-12-10 20:34:34', 1, '2019-12-10 20:34:34', 1);
INSERT INTO `tb_criterion` VALUES (131, 'GB/T 7918.3-1987 《化妆品微生物标准检验方法 粪大肠菌群》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '粪大肠菌群菌来源于人和温血动物的类便。检出类大肠菌群表明该化妆品已被类便污染，有可能存在其他肠道致病菌或寄生虫等病原体的危险，因此类大肠菌被列为重要的卫生指标菌。', 1, '2019-12-10 20:35:30', 1, '2019-12-10 20:35:30', 1);
INSERT INTO `tb_criterion` VALUES (132, 'GB/T 7918.4-1987 《化妆品微生物标准检验方法 绿脓杆菌》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '绿脓杆菌绿脓杆菌在自然界分布甚广，空气、水、土壤中均有存在。对人有致病力，常引起人皮肤化脓感染，特别是烧伤、烫伤、眼部疾病患者被感染后，常使病情恶化，并可引起败血症，因此，在化妆品卫生标准中规定不得检出绿脓杆菌。', 1, '2019-12-10 20:36:28', 1, '2019-12-10 20:36:28', 1);
INSERT INTO `tb_criterion` VALUES (133, 'GB/T 7918.5-1987 《化妆品微生物标准检验方法 金黄色葡萄球菌》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', 'd196d5428731721ebb45c938b66b21f5', '1987-05-28', '1987-10-01', '金黄色葡萄球菌在外界分布较广，抵抗力也较强，能引起人体局部化脓性病灶，严重时可导致败血症，因此化妆品中检验金黄色葡萄球菌有重要意义。', 1, '2019-12-10 20:37:23', 1, '2019-12-10 20:37:23', 1);
INSERT INTO `tb_criterion` VALUES (134, 'GB/T 17149.1-1997 《化妆品皮肤病诊断标准及处理原则 总则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于使用化妆品所引起的皮肤及其附属器的病变。', 1, '2019-12-10 20:38:41', 1, '2019-12-10 20:38:41', 1);
INSERT INTO `tb_criterion` VALUES (135, 'GB/T 17149.2-1997 《化妆品接触性皮炎诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于化妆品引起的刺激性接触性皮炎和变应性接触性皮炎。', 1, '2019-12-10 20:39:29', 1, '2019-12-10 20:39:29', 1);
INSERT INTO `tb_criterion` VALUES (136, 'GB/T 17149.3-1997 《化妆品痤疮诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于因使用化妆品引起的皮肤痤疮样损害。', 1, '2019-12-10 20:40:20', 1, '2019-12-10 20:40:20', 1);
INSERT INTO `tb_criterion` VALUES (137, 'GB/T 17149.4-1997 《化妆品毛发损害诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于化妆品引起的毛发损害。', 1, '2019-12-10 20:41:09', 1, '2019-12-10 20:41:09', 1);
INSERT INTO `tb_criterion` VALUES (138, 'GB/T 17149.5-1997 《化妆品甲损害 诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于化妆品引起的甲及甲周组织损害，不适用于甲及甲周组织非化妆品病变及其指(趾)甲病。', 1, '2019-12-10 20:42:04', 1, '2019-12-10 20:42:04', 1);
INSERT INTO `tb_criterion` VALUES (139, 'GB/T 17149.6-1997 《化妆品光感性皮炎诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于化妆品引起的光感性皮炎。', 1, '2019-12-10 20:42:58', 1, '2019-12-10 20:42:58', 1);
INSERT INTO `tb_criterion` VALUES (140, 'GB/T 17149.7-1997 《化妆品皮肤色素异常诊断标准及处理原则》', '1574147128834', '1574147128843', '9b590ce928bc74cca2df7bed42d98f64', '6d06bb1f6f7ea043e0a378f1760e9dc2', '1997-12-15', '1998-12-01', '适用于因使用化妆品引起的皮肤色素沉着或色素脱失等异常改变。', 1, '2019-12-10 20:43:50', 1, '2019-12-10 20:43:50', 1);
INSERT INTO `tb_criterion` VALUES (141, 'GB/T 26516-2011 《按摩精油》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2011-05-12', '2011-12-01', '适用于需要经过按摩基础油稀释后方可用于人体皮肤按摩和护理的按摩精油产品。', 1, '2019-12-10 20:45:14', 1, '2019-12-10 20:45:14', 1);
INSERT INTO `tb_criterion` VALUES (142, 'GB/T 27574-2011 《睫毛膏》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2011-05-12', '2011-12-01', '适用于睫毛膏产品。', 1, '2019-12-10 20:46:24', 1, '2019-12-10 20:46:24', 1);
INSERT INTO `tb_criterion` VALUES (143, 'GB/T 27575-2011 《化妆笔、化妆笔芯》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2011-05-12', '2011-12-01', '适用于由各种油、脂、蜡、粉类原料与颜料配制，经过研磨挤压、成型或其他方式制成的用于人体表面的美容化妆笔和化妆笔芯。如眼线笔、眉笔、眼影笔、唇线笔、遮瑕笔等，不适用于内装物为液状的化妆笔。', 1, '2019-12-10 20:48:22', 1, '2019-12-10 20:48:22', 1);
INSERT INTO `tb_criterion` VALUES (144, 'GB/T 27576-2011 《唇彩、唇油》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2011-05-12', '2011-12-01', '适用于以油脂、蜡、色素、增稠剂或凝固剂等原料制成的唇彩和唇油。', 1, '2019-12-10 20:49:11', 1, '2019-12-10 20:49:11', 1);
INSERT INTO `tb_criterion` VALUES (145, 'GB/T 29666-2013 《化妆品用防腐剂   甲基氯异噻唑啉酮和甲基异噻唑啉酮与氯化镁及硝酸镁的混合物》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于以甲基氯异噻唑啉酮和甲基异噻唑啉酮作为活性物,以硝酸镁及反应生成的少量氯化镁作为稳定剂的化妆品用防腐剂，该防腐剂以N,N’-二甲基二硫代二丙酰胺为主要原料,经氯化环化制得。', 1, '2019-12-10 20:51:43', 1, '2019-12-10 20:51:43', 1);
INSERT INTO `tb_criterion` VALUES (146, 'GB/T 29667-2013 《化妆品用防腐剂 咪唑烷基脲》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于咪唑烷基脲，咪唑烷基脲以甲醛和尿囊素为原料,在碱性条件下反应生成,应用为化妆品的防腐剂。', 1, '2019-12-10 20:53:26', 1, '2019-12-10 20:53:26', 1);
INSERT INTO `tb_criterion` VALUES (147, 'GB/T 29668-2013 《化妆品用防腐剂 双(羟甲基)咪唑烷基脲》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-02-15', '适用于双(羟甲基)咪唑烷基脲。双(羟甲基)咪唑烷基脲以甲醛和尿囊素为原料,在碱性条件下反应生成,应用为化妆品的防腐剂。', 1, '2019-12-10 20:55:33', 1, '2019-12-10 20:55:33', 1);
INSERT INTO `tb_criterion` VALUES (148, 'GB/T 29990-2013 《润肤油》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-11-27', '2014-05-01', '适用于滋润、保护皮肤为主要目的的油状产品。', 1, '2019-12-10 20:57:14', 1, '2019-12-10 20:57:14', 1);
INSERT INTO `tb_criterion` VALUES (149, 'GB/T 29680-2013 《洗面奶、洗面膏》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-08-01', '适用于以清洁面部皮肤为主要目的的洗面奶、洗面膏。', 1, '2019-12-10 20:59:10', 1, '2019-12-10 20:59:10', 1);
INSERT INTO `tb_criterion` VALUES (150, 'GB/T 29991-2013 《香粉（蜜粉）》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-11-27', '2014-05-01', '适用于香粉（蜜粉）。', 1, '2019-12-10 21:00:09', 1, '2019-12-10 21:00:09', 1);
INSERT INTO `tb_criterion` VALUES (151, 'GB/T 29679-2013 《洗发液、洗发膏》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-08-01', '适用于以表面活性剂为主要活性成分复配而成的、具有清洁人的头皮和头发、并保持其美观作用的洗发液和洗发膏产品。', 1, '2019-12-10 21:01:00', 1, '2019-12-10 21:01:00', 1);
INSERT INTO `tb_criterion` VALUES (152, 'GB/T 29678-2013 《烫发剂》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-08-01', '适用于仅以巯基乙酸及其盐类为还原剂，以过氧化氢或溴酸钠为氧化剂，添加各种辅料配制而成的美发用化学烫发剂产品。', 1, '2019-12-10 21:01:59', 1, '2019-12-10 21:01:59', 1);
INSERT INTO `tb_criterion` VALUES (153, 'GB/T 29665-2013 《护肤乳液》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2013-09-06', '2014-08-01', '适用于护理人体皮肤的具有流动性的乳化型化妆品，不适用于多层型产品。', 1, '2019-12-10 21:02:48', 1, '2019-12-10 21:02:48', 1);
INSERT INTO `tb_criterion` VALUES (154, 'GB/T 30928-2014 《去角质啫喱》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于去角质啫喱产品。', 1, '2019-12-10 21:04:13', 1, '2019-12-10 21:04:13', 1);
INSERT INTO `tb_criterion` VALUES (155, 'GB/T 30941-2014 《剃须膏、剃须凝胶》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2014-07-08', '2014-11-01', '适用于辅助剃须的剃须膏和剃须凝胶产品，不适用于充有推进剂的剃须膏和剃须凝胶产品。', 1, '2019-12-10 21:05:04', 1, '2019-12-10 21:05:04', 1);
INSERT INTO `tb_criterion` VALUES (156, 'GB/T 33306-2016 《化妆品用原料 D-泛醇》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2016-12-13', '2017-07-01', '适用于由D-泛解酸内酯（包括微生物酶法或化学法制得D-泛解酸内酯）和3-氨基丙醇反应制得的化妆品用原料D-泛醇。', 1, '2019-12-10 21:06:11', 1, '2019-12-10 21:06:11', 1);
INSERT INTO `tb_criterion` VALUES (157, 'GB/T 34819-2017 《化妆品用原料 甲基异噻唑啉酮》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2017-11-01', '2018-05-01', '适用于以N，N\'-二甲基二硫代二丙酰胺为主要原料，经氯化环化制得的10%甲基异噻哗啉酮水溶液，可作为防腐剂用于化妆品。', 1, '2019-12-10 21:07:06', 1, '2019-12-10 21:07:06', 1);
INSERT INTO `tb_criterion` VALUES (158, 'GB/T 34820-2017 《化妆品用原料 乙二醇二硬脂酸酯》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2017-11-01', '2018-05-01', '适用于以硬脂酸和乙二醇为原料通过化学合成方法制造的，应用于化妆品珠光剂的乙二醇二硬脂酸酯。', 1, '2019-12-10 21:07:53', 1, '2019-12-10 21:07:53', 1);
INSERT INTO `tb_criterion` VALUES (159, 'GB/T 34857-2017 《沐浴剂》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2017-11-01', '2018-05-01', '适用于以表面活性剂为有效成分配制的，用于人体肌肤清洁的产品，形态包括液体状、膏状、固体状（香皂除外）。', 1, '2019-12-10 21:09:50', 1, '2019-12-10 21:09:50', 1);
INSERT INTO `tb_criterion` VALUES (160, 'GB/T 35889-2018 《眼线液(膏)》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于以着色剂、成膜剂为主要原料，以美化或修饰眼部的眼线液（膏）。', 1, '2019-12-10 21:10:54', 1, '2019-12-10 21:10:54', 1);
INSERT INTO `tb_criterion` VALUES (161, 'GB/T 34855-2017 《洗手液》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2017-11-01', '2018-05-01', '适用于以表面活性剂为有效成分配置的用于手部清洁的产品，不适用于非水洗型产品。', 1, '2019-12-10 21:13:59', 1, '2019-12-10 21:13:59', 1);
INSERT INTO `tb_criterion` VALUES (162, 'GB/T 35914-2018 《卸妆油（液、乳、膏、霜）》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于以卸除彩妆，清洁皮肤为主要目的的卸妆油、乳、膏、霜，不适用于油包水的卸妆乳、霜产品。', 1, '2019-12-10 21:15:02', 1, '2019-12-10 21:15:02', 1);
INSERT INTO `tb_criterion` VALUES (163, 'GB/T 35915-2018 《化妆品用原料 珍珠提取物》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于以淡水珍珠为原料，经粉碎、酶解等工艺制成的珍珠提取物。', 1, '2019-12-10 21:16:12', 1, '2019-12-10 21:16:12', 1);
INSERT INTO `tb_criterion` VALUES (164, 'GB/T 35955-2018 《抑汗(香体)液（乳、喷雾、膏）》', '1574147128834', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128844', '2018-02-06', '2018-09-01', '适用于以氯化羟错铝配合物[AlxZr（OH）yClz]（喷雾产品除外）和/或氯化羟错铝甘氨酸配合物（喷雾产品除外）和/或苯酚磺酸锌和/或氯化羟铝为功效成分的，具有抑汗功效或抑汗兼具香体功效的抑汗（香体）液、抑汗（香体）乳、抑汗（香体）喷雾、抑汗（香体）膏。', 1, '2019-12-10 21:17:02', 1, '2019-12-10 21:17:02', 1);
INSERT INTO `tb_criterion` VALUES (165, 'QB/T 1643-1998 《发用摩丝》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '30c314c43564c6efcc5b774f1e2646e5', '1998-11-25', '1999-12-01', '适用于以丙丁烷或含有二甲醚的混合气体为抛射剂，以高分子聚合物等为原料配制的，用于固定发型或保护、修饰、美化发型的摩丝。', 1, '2019-12-10 21:19:21', 1, '2019-12-10 21:19:21', 1);
INSERT INTO `tb_criterion` VALUES (166, 'QB/T 1644-1998 《定型发胶》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '30c314c43564c6efcc5b774f1e2646e5', '1998-11-25', '1999-12-01', '适用于以高分子聚合物等原料配制而成为固定修饰、美化发型的液体喷发胶。', 1, '2019-12-10 21:20:20', 1, '2019-12-10 21:20:20', 1);
INSERT INTO `tb_criterion` VALUES (167, 'QB/T 1858-2004 《香水、古龙水》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2004-12-14', '2005-06-01', '适用于卫生化妆用的香水和古龙水。', 1, '2019-12-10 21:22:04', 1, '2019-12-10 21:22:04', 1);
INSERT INTO `tb_criterion` VALUES (168, 'QB/T 1862-2011 《发油》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2011-12-20', '2012-07-01', '适用于以矿油、有机硅氧烷、动植物油脂、合成油脂及其他保湿成分等配制而成的具有滋润、保护和美化头发的发用产品。', 1, '2019-12-10 21:23:37', 1, '2019-12-10 21:23:37', 1);
INSERT INTO `tb_criterion` VALUES (169, 'QB/T 1976-2004 《化妆粉块》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2004-12-14', '2005-06-01', '适用于以粉质为主体经压制成型的胭脂、眼影、粉饼等。', 1, '2019-12-10 21:25:22', 1, '2019-12-10 21:25:22', 1);
INSERT INTO `tb_criterion` VALUES (170, 'QB/T 1857-2013 《润肤膏霜》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2013-12-31', '2014-07-01', '适用于滋润人体皮肤（或以滋润人体皮肤为主兼具修饰作用）的具有一定稠度的乳化型膏霜。', 1, '2019-12-10 21:29:21', 1, '2019-12-10 21:29:21', 1);
INSERT INTO `tb_criterion` VALUES (171, 'QB/T 1858.1-2006 《花露水》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2006-12-17', '2007-08-01', '适用于由乙醇、水、香精和（或）添加剂等成分配置而成的产品。', 1, '2019-12-10 21:31:30', 1, '2019-12-10 21:31:30', 1);
INSERT INTO `tb_criterion` VALUES (172, 'QB/T 1977-2004 《唇膏》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2004-12-14', '2005-06-01', '适用于油、脂、蜡、色素等主要成分复配而成的护唇用品。', 1, '2019-12-10 21:35:01', 1, '2019-12-10 21:35:01', 1);
INSERT INTO `tb_criterion` VALUES (173, 'QB/T 1978-2016 《染发剂》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于能使头发改变颜色的氧化型和非氧化型染发剂（其中包括使用后即时清洗能恢复头发原有颜色的产品和即时清洗不能恢复头发原有颜色的产品），不适用于发用漂浅剂、含有抛射剂的具有一定压力的气雾罐染发剂产品及含参与染发过程的多罐染剂、氧化剂的三剂型及以上产品。', 1, '2019-12-10 21:36:11', 1, '2019-12-10 21:36:11', 1);
INSERT INTO `tb_criterion` VALUES (174, 'QB/T 2284-2011 《发乳》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2011-12-20', '2012-07-01', '适用于发乳产品。', 1, '2019-12-10 21:37:50', 1, '2019-12-10 21:37:50', 1);
INSERT INTO `tb_criterion` VALUES (175, 'QB/T 2287-2011 《指甲油》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2011-12-20', '2012-07-01', '适用于以溶剂、成膜剂等原料制成的美化、修饰、护理指甲（趾甲）用的稠状液体产品。', 1, '2019-12-10 21:39:12', 1, '2019-12-10 21:39:12', 1);
INSERT INTO `tb_criterion` VALUES (176, 'QB/T 2660-2004 《化妆水》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2004-12-14', '2005-06-01', '适用于补充皮肤所需水分、保护皮肤的水剂型护肤品。', 1, '2019-12-10 21:40:35', 1, '2019-12-10 21:40:35', 1);
INSERT INTO `tb_criterion` VALUES (177, 'QB/T 2488-2006 《化妆品用芦荟汁、粉》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2006-12-17', '2007-08-01', '适用于以芦荟（Aloe vera L.）为原料，经清洗、榨汁、杀菌等工序制成的芦荟汁、粉。', 1, '2019-12-10 21:42:01', 1, '2019-12-10 21:42:01', 1);
INSERT INTO `tb_criterion` VALUES (178, 'QB/T 2872-2017 《面膜》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2017-04-12', '2017-10-01', '适用于涂或敷于人体皮肤表面，经一段时间后揭离、擦洗或保留，起到集中护理或清洁作用的面膜产品，泡沫面膜及经发酵工艺制成的纤维类贴膜不适用于本标准。', 1, '2019-12-10 21:45:05', 1, '2019-12-10 21:45:05', 1);
INSERT INTO `tb_criterion` VALUES (179, 'QB/T 2873-2007 《发用啫喱（水）》及第1号修改单', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2007-10-08', '2008-03-01', '适用于以高分子聚合物为主要原料配制而成，对头发起到定型和护理作用的凝胶或液体状发用啫喱（水）。', 1, '2019-12-10 21:46:58', 1, '2019-12-10 21:46:58', 1);
INSERT INTO `tb_criterion` VALUES (180, 'QB/T 2874-2007 《护肤啫喱》及第1号修改单', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128845', '2007-10-08', '2008-03-01', '适用于以护理人体皮肤为主要目的的护肤啫喱，其配方中主要使用高分子聚合物为凝胶剂。', 1, '2019-12-10 21:49:01', 1, '2019-12-10 21:49:01', 1);
INSERT INTO `tb_criterion` VALUES (181, 'QB/T 4076-2010 《发蜡》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2010-11-22', '2011-03-01', '适用于蜡或/和油、脂、水等配制而成的发蜡产品。', 1, '2019-12-10 21:50:24', 1, '2019-12-10 21:50:24', 1);
INSERT INTO `tb_criterion` VALUES (182, 'QB/T 4077-2010 《焗油膏（发膜）》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2010-11-22', '2011-03-01', '适用于以抗静电剂或柔软剂和各种护发剂配置而成的凝胶状或乳膏状产品，能深度滋养、修护头发，改善发质、使头发有光泽且易于梳理的发用产品，不适用于永久性、涂染型暂时性染发剂类的焗油膏（发膜）。', 1, '2019-12-10 21:51:19', 1, '2019-12-10 21:51:19', 1);
INSERT INTO `tb_criterion` VALUES (183, 'QB/T 4079-2010 《按摩基础油、按摩油》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2010-11-22', '2011-03-01', '适用于按摩基础油、按摩油产品，不适用于儿童按摩产品和眼部按摩产品。', 1, '2019-12-10 21:52:07', 1, '2019-12-10 21:52:07', 1);
INSERT INTO `tb_criterion` VALUES (184, 'QB/T 4126-2010 《发用漂浅剂》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2010-12-29', '2011-04-01', '适用于以过硫化物为主体复配而成，漂剂为粉状或湿润粉状的，具有漂浅头发功能的发用产品。', 1, '2019-12-10 21:53:19', 1, '2019-12-10 21:53:19', 1);
INSERT INTO `tb_criterion` VALUES (185, 'QB/T 4364-2012 《洗甲液》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2012-11-07', '2013-03-01', '适用于以乙酸乙酯、丙酮、碳酸丙二醇酯等溶剂为主要原料制成用于卸除甲油的洗甲液产品。', 1, '2019-12-10 21:54:39', 1, '2019-12-10 21:54:39', 1);
INSERT INTO `tb_criterion` VALUES (186, 'QB/T 4416-2012 《化妆品用原料 透明质酸钠》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2012-12-28', '2013-06-01', '适用于由微生物（马链球菌兽疫亚种）发酵制得的、作为化妆品原料应用的透明质酸钠（一种黏多糖类物质）。', 1, '2019-12-10 21:55:56', 1, '2019-12-10 21:55:56', 1);
INSERT INTO `tb_criterion` VALUES (187, 'QB/T 1859-2013 《爽身粉、痱子粉》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2013-12-31', '2014-07-01', '适用于以粉体原料为基质，添加其他辅料成分配制而成的爽身粉、祛痱粉。', 1, '2019-12-10 21:57:41', 1, '2019-12-10 21:57:41', 1);
INSERT INTO `tb_criterion` VALUES (188, 'QB/T 1975-2013 《护发素》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2013-12-31', '2014-07-01', '适用于由抗静电剂、柔软剂和各种护发剂等原料配制而成，用于保护头发、使头发有光泽，易于梳理的乳液状或膏霜状护发产品。', 1, '2019-12-10 21:58:56', 1, '2019-12-10 21:58:56', 1);
INSERT INTO `tb_criterion` VALUES (189, 'QB/T 5108-2017 《脱毛霜（乳）》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2017-04-12', '2017-10-01', '适用于以脱毛为主要目的具有一定稠度或流动性的乳化型膏霜、乳液类产品。', 1, '2019-12-10 22:00:23', 1, '2019-12-10 22:00:23', 1);
INSERT INTO `tb_criterion` VALUES (190, 'QB/T 4947-2016 《化妆品用原料 三氯生》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于以2,4-二氯苯酚和2,5-二氯硝基苯为起始原料，经缩合、还原、重氮化、水解、提纯等工艺路线所制得的, 起抗菌和防腐作用的个人护理品用原料。', 1, '2019-12-10 22:01:54', 1, '2019-12-10 22:01:54', 1);
INSERT INTO `tb_criterion` VALUES (191, 'QB/T 4948-2016 《化妆品用原料 月桂醇磷酸酯》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于以月桂醇与五氧化二磷、三氯化磷、五氯化磷或聚磷酸为原料制得的月桂醇磷酸酯产品，它是阴离子型表面活性剂，在化妆品中用作发泡剂和清洗剂，不适用于作为在化妆品配方中用作需高比例双酯产品的如乳化剂等其他用途的原料。', 1, '2019-12-10 22:03:32', 1, '2019-12-10 22:03:32', 1);
INSERT INTO `tb_criterion` VALUES (192, 'QB/T 4949-2016 《化妆品用原料 脂肪酰二乙醇胺》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于化妆品用由椰子油、棕榈仁油及其衍生物与二乙醇胺反应生成的脂肪酰二乙醇胺。', 1, '2019-12-10 22:04:33', 1, '2019-12-10 22:04:33', 1);
INSERT INTO `tb_criterion` VALUES (193, 'QB/T 4950-2016 《化妆品用原料 PCA钠》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于以谷氨酸钠为原料通过化学半合成方法制造的，应用于化妆品保湿剂的50%PCA钠水溶液。', 1, '2019-12-10 22:06:06', 1, '2019-12-10 22:06:06', 1);
INSERT INTO `tb_criterion` VALUES (194, 'QB/T 4951-2016 《化妆品用原料 光果甘草（Glycyrrhiza   glabra）根提取物》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于以乙酸乙酯和乙醇为溶剂，从光果甘草根中提取的用作化妆品美白剂的光果甘草根提取物，其主要成份为光甘草定。', 1, '2019-12-10 22:07:08', 1, '2019-12-10 22:07:08', 1);
INSERT INTO `tb_criterion` VALUES (195, 'QB/T 4952-2016 《化妆品用原料 抗坏血酸磷酸酯镁》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于以抗坏血酸为原料经与三氯氧化磷（POCl3)化学反应合成的，应用于化妆品的抗坏血酸磷酸酯镁。', 1, '2019-12-10 22:07:55', 1, '2019-12-10 22:07:55', 1);
INSERT INTO `tb_criterion` VALUES (196, 'QB/T 4953-2016 《化妆品用原料 熊果苷（β -熊果苷）》', '1574147128835', '1574147128840', '9b590ce928bc74cca2df7bed42d98f64', '1574147128846', '2016-04-05', '2016-09-01', '适用于由β-五乙酰葡萄糖与对苯二酚反应，再经酯交换反应合成的，应用于化妆品美白剂的β-熊果苷。', 1, '2019-12-10 22:08:39', 1, '2019-12-10 22:08:39', 1);
